import React, { useEffect, useRef } from 'react';

function PopupForm(props) {
  const submitButtonRef = useRef(null);

  useEffect(() => {
    if (props.isOpen) {
      setTimeout(() => {
        submitButtonRef.current.focus();
      }, 0);
    }
  }, [props.isOpen]);

  return (
    <section className={`popup popup_base_light ${props.isOpen && 'popup_opened'}`}>
      <div className={`popup__container popup__container_type_form ${props.isOpen ? 'grow' : 'shrink'}`}>
        <button
          className="popup__close-button" type="button" aria-label="Закрыть." onClick={props.onClose}
        />
        <h2 className="popup__title">{props.title}</h2>
        {props.validate ? (
          <FormWithValidation onSubmit={props.onSubmit} {...props}>
            {props.children}
          </FormWithValidation>
        ) : (
          <form
            className="form"
            name={props.name}
            onSubmit={props.onSubmit}
            noValidate
          >
            {props.isLoading ? (
              <Spinner />
            ) : (
              <button
                className="form__submit"
                type="submit"
                name="submit"
                ref={submitButtonRef}
                autoFocus // Keep autoFocus attribute
              >
                {props.submitText || 'Сохранить'}
              </button>
            )}
          </form>
        )}
      </div>
    </section>
  );
}

export default PopupForm;
