import React, {useState, useEffect} from 'react';

const InputWithValidation = React.forwardRef((props, ref) => {
  const [inputValue, setInputValue] = useState('');
  const [isInputValid, setIsInputValid] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    setInputValue(props.defaultValue);
    setIsInputValid(true);
    setErrorMessage('');
  }, [props.isShown]);

  function handleInputsChange(event) {
    setInputValue(event.target.value);
    setIsInputValid(event.target.validity.valid);
    setErrorMessage(event.target.validationMessage);

    props.onUpdate(name, event.target.value);
  }

  return (
    <>
      <input
        ref={ref}
        className={`form__input ${!isInputValid && 'form__input_error'}`}
        name={name}
        onChange={handleInputsChange}
        value={inputValue || ''}
        {...props}
      />
      <span className="form__input-error">{!isInputValid && errorMessage}</span>
    </>
  );
})

export default InputWithValidation;
