import React, {useRef} from 'react';

import FormWithValidation from './FormWithValidation';
import Spinner from './Spinner';


const PopupWithForm = React.memo((props) => {
  const submitButtonRef = useRef();

  if (!props.validate && props.isOpen) {
    setTimeout(() => submitButtonRef.current?.focus(), 0);
  }

  return (
    <section ref={popupRef} className={`popup popup_base_light ${props.isOpen && 'popup_opened'}`}>
      <div className={`popup__container popup__container_type_form ${props.isOpen ? 'grow' : 'shrink'}`}>
        <button
          className="popup__close-button" type="button" aria-label="Закрыть." onClick={props.onClose}
        />
        <h2 className="popup__title">{props.title}</h2>
        {props.validate
          ? <FormWithValidation onSubmit={props.onSubmit} {...props}>
            {props.children}
          </FormWithValidation>
          : <form
            className="form"
            name={props.name}
            onSubmit={props.onSubmit}
            noValidate
            autoFocus
          >
            {props.isLoading
              ? <Spinner />
              : <button
                ref={submitButtonRef}
                className="form__submit"
                type="submit"
                name="submit"
              >{props.submitText || 'Сохранить'}</button>}
          </form>
        }

      </div>
    </section>
  );
})

export default PopupWithForm;
