import {useState, useEffect, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import PopupWithForm from './PopupWithForm';

function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const [name, setName] = useState('');
  const [job, setJob] = useState('');

  function handleNameChange(event) {
    setName(event.target.value);
  }

  function handleJobChange(event) {
    setJob(event.target.value);
  }

  function handleSubmit(e) {
    // Запрещаем браузеру переходить по адресу формы
    e.preventDefault();

    // Передаём значения управляемых компонентов во внешний обработчик
    props.onUpdateUser({
      name,
      about: description,
    });
  }

  useEffect(() => {
    setName(currentUser.name);
    setJob(currentUser.about);
  }, [currentUser]);

  return (
    <PopupWithForm name="edit-profile" isOpen={props.isOpen} onSubmit={handleSubmit} onClose={props.onClose}>
      <input
        id="profile-name-input"
        className="form__input form__input_type_profile-name"
        aria-label="Имя."
        onChange={handleNameChange}
        type="text"
        name="userName"
        value={name}
        placeholder="Имя"
        minLength="2"
        maxLength="40"
        required
      />
      {/*<span className="profile-name-input-error form__input-error"></span>*/}
      <input
        id="profile-job-input"
        className="form__input form__input_type_profile-job"
        aria-label="Деятельность."
        onChange={handleJobChange}
        type="text"
        name="userJob"
        value={job}
        placeholder="Описание"
        minLength="2"
        maxLength="200"
        required
      />
      {/*<span className="profile-job-input-error form__input-error"></span>*/}
    </PopupWithForm>
  );
}

export default EditProfilePopup;
