import {useState, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import PopupWithForm from './PopupWithForm';

function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const [value, setValue] = useState('');
  function handleChange(event) {
    setValue(event.target.value);
  }

  return (
    <PopupWithForm isOpen={props.isOpen} onClose={props.onClose}>
      <input
        id="profile-name-input"
        className="form__input form__input_type_profile-name"
        aria-label="Имя."
        onChange={handleChange}
        type="text"
        name="userName"
        value={value}
        placeholder="Имя"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="profile-name-input-error form__input-error"></span>
      <input
        id="profile-job-input"
        className="form__input form__input_type_profile-job"
        aria-label="Деятельность."
        onChange={handleChange}
        type="text"
        name="userJob"
        value={value}
        placeholder="Описание"
        minLength="2"
        maxLength="200"
        required
      />
      <span className="profile-job-input-error form__input-error"></span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
