import {useState, useEffect, useRef, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import PopupWithForm from './PopupWithForm';


function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const inputRefs = useRef({});

  const [inputErrors, setInputErrors] = useState({name: '', about: ''});
  const [inputInfo, setInputInfo] = useState({name: '', about: ''});


  // handle change

  function handleInputsChange(event) {
    setInputInfo({...inputInfo,
      [event.target.name]: event.target.value
    });
  }

  function handleError(input, message) {
    setInputErrors({...inputErrors,
      [input]: message
    })
  }

  console.log(inputErrors);

  // handle submit

  function handleSubmit() {
    props.onUpdateUser({
      name: inputInfo.name,
      about: inputInfo.about
    });
  }


  // effects

  useEffect(() => {
    setInputInfo({...inputInfo,
      name: currentUser.name,
      about: currentUser.about
    })
  }, [currentUser, props.isOpen]);


  return (
    <PopupWithForm
      title="Редактировать профиль"
      name="edit-profile"
      onError={handleError}
      isOpen={props.isOpen}
      inputRefs={inputRefs}
      submitText={props.isLoading ? props.loadingText : 'Сохранить'}
      onSubmit={handleSubmit}
      ariaLabel="Сохранить."
      {...props}
    >
      <input
        ref={(ref) => (inputRefs.current['name'] = ref)}
        className="form__input"
        aria-label="Имя."
        onChange={handleInputsChange}
        type="text"
        name="name"
        value={inputInfo.name || ''}
        placeholder="Имя"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="form__input-error">{inputErrors.name}</span>
      <input
        ref={(ref) => (inputRefs.current['about'] = ref)}
        className="form__input"
        aria-label="Деятельность."
        onChange={handleInputsChange}
        type="text"
        name="about"
        value={inputInfo.about || ''}
        placeholder="Описание"
        minLength="2"
        maxLength="200"
        required
      />
      <span className="form__input-error">{inputErrors.about}</span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
