import {useState, useEffect, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import FormWithValidation from './FormWithValidation';
import InputWithValidation from './InputWithValidation';
import Spinner from './Spinner';


function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const [inputValues, setInputValues] = useState({});


  // handle change

  function handleValuesUpdate(name, value) {
    setInputValues((prevValues) => ({
      ...prevValues, [name]: value
    }));
  }


  // handle submit

  function handleSubmit() {
    props.onUpdateUser({
      name: inputValues.userName,
      about: inputValues.userAbout
    });
  }


  // effects

  useEffect(() => {
    setInputValues({
      userName: currentUser.name,
      userAbout: currentUser.about
    })
  }, [currentUser]);


  return (
    <section className={`popup popup_base_light ${props.isOpen && 'popup_opened'}`}>
      <div className={`popup__container popup__container_type_form ${props.isOpen ? 'grow' : 'shrink'}`}>
        <button
          className="popup__close-button"
          type="button"
          aria-label="Закрыть."
          onClick={props.onClose}
        />
        <h2 className="popup__title">{props.title}</h2>
        <FormWithValidation
          name="edit-profile"
          onSubmit={handleSubmit}
          isLoading={props.isLoading}
          submitText="Сохранить"
          ariaLabel="Сохранить."
          noValidate
        >
          <InputWithValidation
            className="form__input"
            aria-label="Имя."
            isShown={props.isOpen}
            defaultValue={currentUser.name}
            onUpdate={handleValuesUpdate}
            type="text"
            name="userName"
            placeholder="Имя"
            minLength="2"
            maxLength="40"
            required
          />
          <InputWithValidation
            className="form__input"
            aria-label="Деятельность."
            isShown={props.isOpen}
            defaultValue={currentUser.about}
            onUpdate={handleValuesUpdate}
            type="text"
            name="userAbout"
            placeholder="Описание"
            minLength="2"
            maxLength="200"
            required
          />
        </FormWithValidation>
      </div>
    </section>
  );
}

export default EditProfilePopup;
