import {useState, useEffect, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import PopupWithForm from './PopupWithForm';
import InputWithValidation from './InputWithValidation';


function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const [inputInfo, setInputInfo] = useState({name: '', about: ''});


  // handle change

  // function handleInputsChange(event) {
  //   setInputInfo({...inputInfo,
  //     [event.target.name]: event.target.value
  //   })
  // }

  function handleValuesUpdate({name, value}) {
    setInputInfo({...inputInfo,
      name: value
    })
  }


  // handle submit

  function handleSubmit() {
    props.onUpdateUser({
      name: inputInfo.name,
      about: inputInfo.about
    });
  }


  // effects

  useEffect(() => {
    setInputInfo({...inputInfo,
      name: currentUser.name,
      about: currentUser.about
    })
  }, [currentUser, props.isOpen]);


  return (
    <PopupWithForm
      title="Редактировать профиль"
      inputName="edit-profile"
      submitText={props.isLoading ? props.loadingText : 'Сохранить'}
      onSubmit={handleSubmit}
      ariaLabel="Сохранить."
      {...props}
    >
      <InputWithValidation
        className="form__input"
        aria-label="Имя."
        onUpdate={handleValuesUpdate}
        type="text"
        name="name"
        placeholder="Имя"
        minLength="2"
        maxLength="40"
        required
      />
      <InputWithValidation
        className="form__input"
        aria-label="Деятельность."
        onUpdate={handleValuesUpdate}
        type="text"
        name="about"
        placeholder="Описание"
        minLength="2"
        maxLength="200"
        required
      />
    </PopupWithForm>
  );
}

export default EditProfilePopup;
