import {useState} from 'react';


function FormValidator(props) {
  const [inputErrors, setInputErrors] = useState({name: '', about: ''});

  function handleSubmit(event) {
    event.preventDefault();

    if (event.target.checkValidity()) {
      props.onSubmit();
    }
  }

  if (props.isOpen) {
    const inputsList = Object.values(props.inputRefs.current);

    inputsList.forEach(input => {
      if (!input.validity.valid) {
        input.classList.add('form__input_error');
        // props.onError(input.name, input.validationMessage);
        setInputErrors({...inputErrors, [input.name]: input.validationMessage});
      } else {
        input.classList.remove('form__input_error');
      }
    })
  }

  console.log(inputErrors);

  return (
    <form className="form" name={props.name} onSubmit={handleSubmit} noValidate>
      {props.children}
    </form>
  );
}

export default FormValidator;
