    //   [event.target.name]: event.target.validationMessage
    // })
  }

  // console.log(inputValues[name]);

  return (
    <>
      <input
        {...props}
        // className={`form__input ${!inputsValidity[name] && 'form__input_error'}`}
        className="form__input"
        onChange={handleInputsChange}
        // value={inputValues[name] || ''}
        value={inputValue || ''}
        name={name}
      />
      {/*<span className="form__input-error">{!inputsValidity[name] && errorMessages[name]}</span>*/}
    </>
  );
}

export default InputWithValidation;
