import {useState, useEffect} from 'react';


function FormWithValidation(props) {
  const [isFormValid, setIsFormValid] = useState(false);
  const [inputsValidity, setInputsValidity] = useState({});

  // console.log('inputsValidity', inputsValidity);
  // console.log('isFormValid', isFormValid);

  function handleSubmit(event) {
    event.preventDefault();

    if (event.target.checkValidity()) {
      props.onSubmit();
    }

    setIsFormValid(false);
  }
  //
  function handleChange(event) {
    setInputsValidity((prevValues) => ({
      ...prevValues, [event.target.name]: event.target.validity.valid
    }));
  }

  function validateForm() {
    const inputValues = Object.values(inputsValidity);

    if (inputValues.length === 0) {
      setIsFormValid(false); // Set isFormValid to false if there are no input values
      return;
    }

    const isFormValid = inputValues.every((i) => i === true);
    setIsFormValid(isFormValid);
  }

  useEffect(() => {
    validateForm();
  }, [inputsValidity])

  return (
    <section className={`popup popup_base_light ${props.isOpen && 'popup_opened'}`}>
      <div className={`popup__container popup__container_type_form ${props.isOpen ? 'grow' : 'shrink'}`}>
        <button
          className="popup__close-button"
          type="button"
          aria-label="Закрыть."
          onClick={props.onClose}
        />
        <h2 className="popup__title">{props.title}</h2>
        <form className="form" onChange={handleChange} onSubmit={handleSubmit} noValidate>
          {props.children}
          <button
            className={`form__submit ${!isFormValid && 'form__submit_disabled'}`}
            aria-label={props.ariaLabel}
            type="submit"
            name="submit"
            disabled={!isFormValid}
          >{props.submitText || 'Сохранить'}</button>
        </form>
      </div>
    </section>
  );
}

export default FormWithValidation;
