import React from 'react';

import FormValidator from './FormValidator';

function PopupWithForm(props) {
  return (
    <section className={`popup popup_base_light ${props.isOpen && 'popup_opened'}`}>
      <div className={`popup__container popup__container_type_form ${props.isOpen ? 'grow' : 'shrink'}`}>
        <button
          className="popup__close-button"
          type="button"
          aria-label="Закрыть."
          onClick={props.onClose}
        />
        <h2 className="popup__title">{props.title}</h2>
        <FormValidator name={props.name} inputRefs={props.inputRefs} onSubmit={props.onSubmit}>
          {props.children}
          <button
            className="form__submit"
            aria-label={props.ariaLabel}
            type="submit"
            name="submit"
          >{props.submitText || 'Сохранить'}</button>
        </FormValidator>
      </div>
    </section>
  );
}

export default PopupWithForm;
