import {useState} from 'react';


function InputWithValidation({name, ...props}) {
  const [inputValues, setInputValues] = useState({name: '', value: ''})

  const [inputsValidity, setInputsValidity] = useState({name: '', validity: true});
  const [errorMessages, setErrorMessages] = useState({name: '', message: ''})

  function handleInputsChange(event) {
    setInputValues({...inputValues,
      [event.target.name]: event.target.value
    })

    console.log(event.target.name);

    if (!event.target.validity.valid) {
      setInputsValidity({...inputsValidity,
        [event.target.name]: event.target.validity.valid
      })

      setErrorMessages({...errorMessages,
        [event.target.name]: event.target.validationMessage
      })
    } else {

    }
  }

  console.log(inputsValidity);

  return (
    <>
      <input className={`form__input ${inputsValidity[name] && 'form__input_error'}`} onChange={handleInputsChange} value={inputValues[name] || ''} name={name} {...props}/>
      <span className="form__input-error">{inputsValidity[name] && errorMessages[name]}</span>
    </>
  );
}

export default InputWithValidation;
