import {useState} from 'react';


function InputWithValidation({name, ...props}) {
  const [inputValues, setInputValues] = useState({})

  const [inputsValidity, setInputsValidity] = useState({});
  const [errorMessages, setErrorMessages] = useState({})

  console.log('inputsValidity', Boolean(inputsValidity));

  function handleInputsChange(event) {
    setInputValues({...inputValues,
      [event.target.name]: event.target.value
    })

    setInputsValidity({...inputsValidity,
      [event.target.name]: event.target.validity.valid
    })

    setErrorMessages({...errorMessages,
      [event.target.name]: event.target.validationMessage
    })
  }

  return (
    <>
      <input
        {...props}
        className={`form__input ${!inputsValidity[name] && 'form__input_error'}`}
        onChange={handleInputsChange}
        value={inputValues[name] || ''}
        name={name}
      />
      <span className="form__input-error">{!inputsValidity[name] && errorMessages[name]}</span>
    </>
  );
}

export default InputWithValidation;
