import {useState, useEffect, useContext} from 'react';

import {CurrentUserContext} from '../contexts/CurrentUserContext';

import PopupWithForm from './PopupWithForm';


function EditProfilePopup(props) {
  const currentUser = useContext(CurrentUserContext);

  const [profileInfo, setProfileInfo] = useState({name: '', about: ''});

  const [isFormValid, setIsFormValid] = useState(false);

  const [inputsValidity, setInputsValidity] = useState({name: true, about: true})
  const [errorMessages, setErrorMessages] = useState({name: '', about: ''});


  // handle change

  function handleProfileInfoChange(event) {
    setProfileInfo({...profileInfo,
      [event.target.name]: event.target.value
    });

    // inputs validation

    if (!event.target.validity.valid) {
      setIsFormValid(false);

      setInputsValidity({...inputsValidity,
        [event.target.name]: event.target.validity.valid
      });

      setErrorMessages({...errorMessages,
        [event.target.name]: event.target.validationMessage
      })
    } else {
      setIsFormValid(true);

      setInputsValidity({...inputsValidity,
        [event.target.name]: event.target.validity.valid
      });
    }
  }


  // handle submit

  function handleSubmit(event) {
    event.preventDefault();

    props.onUpdateUser({
      name: profileInfo.name,
      about: profileInfo.about
    });

    setIsFormValid(false);
  }


  // effects

  useEffect(() => {
    setProfileInfo({...profileInfo,
      name: currentUser.name,
      about: currentUser.about
    })
  }, [currentUser]);


  return (
    <PopupWithForm
      title="Редактировать профиль"
      name="edit-profile"
      isFormValid={isFormValid}
      submitText={props.isLoading ? props.loadingText : 'Сохранить'}
      onSubmit={handleSubmit}
      ariaLabel="Сохранить."
      {...props}
    >
      <input
        id="profile-name-input"
        className={`form__input ${!inputsValidity.name && 'form__input_error'}`}
        aria-label="Имя."
        onChange={handleProfileInfoChange}
        type="text"
        name="name"
        value={profileInfo.name || ''}
        placeholder="Имя"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="form__input-error">
      {!inputsValidity.name && errorMessages.name}
    </span>
      <input
        id="profile-about-input"
        className={`form__input ${!inputsValidity.about && 'form__input_error'}`}
        aria-label="Деятельность."
        onChange={handleProfileInfoChange}
        type="text"
        name="about"
        value={profileInfo.about || ''}
        placeholder="Описание"
        minLength="2"
        maxLength="200"
        required
      />
      <span className="form__input-error">
      {!inputsValidity.about && errorMessages.about}
    </span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
