import {useState, useEffect} from 'react';


function InputWithValidation({name, ...props}) {
  const [inputValue, setInputValue] = useState('');
  const [isInputValid, setIsInputValid] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');


  function handleInputsChange(event) {
    setInputValue(event.target.value);

    setIsInputValid(event.target.validity.valid);

    setErrorMessage(event.target.validationMessage);

    // setInputValues({...inputValues,
    //   [event.target.name]: event.target.value
    // })
    //
    // setInputsValidity({...inputsValidity,
    //   [event.target.name]: event.target.validity.valid
    // })
    //
    // setErrorMessages({...errorMessages,
    //   [event.target.name]: event.target.validationMessage
    // })
  }

  // console.log(inputValues[name]);

  return (
    <>
      <input
        {...props}
        className={`form__input ${!isInputValid && 'form__input_error'}`}
        onChange={handleInputsChange}
        value={inputValue || ''}
        name={name}
      />
      <span className="form__input-error">{!isInputValid && errorMessage}</span>
    </>
  );
}

export default InputWithValidation;
